import React, { Component } from "react";
import axios from "axios";

const axiosInstance = axios.create({
  headers: {
    "X-CMC_PRO_API_KEY": "80415b01-6c5a-4547-b5d0-f80814428492",
    'Access-Control-Allow-Origin': '*'
  },
  proxy: {
    host: 'localhost',
    port: 3000
  }
});

// ICI Create a Class for web services ? Refactor
// Faire l'appel via mon back end pour enelver l'erreur CORS
function getCryptoByMarketcap() {
  axiosInstance
    .get("https://pro-api.coinmarketcap.com/v1/cryptocurrency/listings/latest?start=1&limit=1&convert=USD")
    .then(response => {
      console.log(response);
    })
    .catch(error => {
      console.log(error);
    });
}

export default getCryptoByMarketcap;
